mapper.select:
select t.*,count(at.tag_id) as count from me_article_tag at
right join me_tag t on t.id = at.tag_id
group by t.id order by count(at.tag_id) desc limit #{limit}

mapper.insert:
insert into me_tag (id, avatar, tagname
  )
values (#{id,jdbcType=INTEGER}, #{avatar,jdbcType=VARCHAR}, #{tagname,jdbcType=VARCHAR}
  )

mapper.update:
update me_tag
set avatar = #{avatar,jdbcType=VARCHAR},
  tagname = #{tagname,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}
